"use client"

// Component for collecting user body details and preferences
import { useState } from "react"
import { apiCall } from "../services/mockApi.js"
import "../styles/UserDetailsForm.css"

// --- Define Model Image URLs ---
const MODEL_IMAGES = {
  male: "/images/model-male.jpg",
  female: "/images/model-female.jpg",
  '3d_model': "/images/model-3d-placeholder.jpg", // New 3D Model Placeholder
}

function UserDetailsForm({ onSubmit }) {
  const [formData, setFormData] = useState({
    name: "",
    height: "",
    weight: "",
    gender: "male",
    shoeSize: "",
    preferredStyle: "casual",
    image: null,
    imagePreview: null,
  })

  const [loading, setLoading] = useState(false)

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }))
  }

  // Handle image upload
  const handleImageUpload = (e) => {
    const file = e.target.files[0]
    if (file) {
      const reader = new FileReader()
      reader.onloadend = () => {
        setFormData((prev) => ({
          ...prev,
          image: reader.result,
          imagePreview: reader.result,
        }))
      }
      reader.readAsDataURL(file)
    }
  }

  // Handle model image selection
  const handleModelImageSelect = (url) => {
    setFormData((prev) => ({
      ...prev,
      image: url,
      imagePreview: url,
    }))
  }

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault()

    // 1. Validate required fields
    if (!formData.name || !formData.height || !formData.weight || !formData.shoeSize) {
      alert("Please fill in all required fields")
      return
    }

    // 2. LOGIC MODIFICATION: Automatically select 3D model if no image is uploaded/selected
    let finalImageData = formData.image

    if (!finalImageData) {
      // Use the 3D model placeholder as the default fallback
      finalImageData = MODEL_IMAGES['3d_model']
      alert("No image uploaded. Using the default 3D Model for visualization.")
    }

    setLoading(true)
    try {
      // NOTE: We use finalImageData for the API call
      const result = await apiCall("/api/user/profile", {
        method: "POST",
        body: JSON.stringify({
          name: formData.name,
          height: Number.parseInt(formData.height),
          weight: Number.parseInt(formData.weight),
          gender: formData.gender,
          shoeSize: formData.shoeSize,
          preferredStyle: formData.preferredStyle,
          uploadedImage: finalImageData, // Use the determined image data
        }),
      })

      if (result.success || result.user) {
        const user = result.user || result
        onSubmit({
          ...formData,
          image: finalImageData, // Ensure the correct image data is passed to onSubmit
          userId: user._id,
        })
      } else {
        alert("Failed to save profile")
      }
    } catch (error) {
      console.error("[v0] Error saving profile:", error)
      alert("Error saving profile: " + error.message)
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="user-details-form">
      <h2>Tell Us About Yourself</h2>

      <form onSubmit={handleSubmit}>
        {/* ... (Other form fields are unchanged) ... */}

        {/* Name */}
        <div className="form-group">
          <label htmlFor="name">Name *</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            placeholder="Enter your name"
            required
          />
        </div>

        {/* Height */}
        <div className="form-group">
          <label htmlFor="height">Height (cm) *</label>
          <input
            type="number"
            id="height"
            name="height"
            value={formData.height}
            onChange={handleInputChange}
            placeholder="e.g., 170"
            required
          />
        </div>

        {/* Weight */}
        <div className="form-group">
          <label htmlFor="weight">Weight (kg) *</label>
          <input
            type="number"
            id="weight"
            name="weight"
            value={formData.weight}
            onChange={handleInputChange}
            placeholder="e.g., 70"
            required
          />
        </div>

        {/* Gender */}
        <div className="form-group">
          <label htmlFor="gender">Gender *</label>
          <select id="gender" name="gender" value={formData.gender} onChange={handleInputChange}>
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="other">Other</option>
          </select>
        </div>

        {/* Shoe Size */}
        <div className="form-group">
          <label htmlFor="shoeSize">Shoe Size *</label>
          <input
            type="text"
            id="shoeSize"
            name="shoeSize"
            value={formData.shoeSize}
            onChange={handleInputChange}
            placeholder="e.g., 42"
            required
          />
        </div>

        {/* Preferred Style */}
        <div className="form-group">
          <label htmlFor="preferredStyle">Preferred Style *</label>
          <select
            id="preferredStyle"
            name="preferredStyle"
            value={formData.preferredStyle}
            onChange={handleInputChange}
          >
            <option value="casual">Casual</option>
            <option value="formal">Formal</option>
            <option value="party">Party</option>
            <option value="traditional">Traditional</option>
            <option value="sporty">Sporty</option>
          </select>
        </div>

        {/* Image Upload */}
        <div className="form-group">
          <label>Upload Your Photo</label>
          <div className="image-upload-section">
            <input type="file" accept="image/*" onChange={handleImageUpload} className="file-input" />
            {/* Display the uploaded/selected image preview */}
            {formData.imagePreview && (
              <div className="image-preview selected">
                <img src={formData.imagePreview} alt="Preview" />
              </div>
            )}
          </div>
        </div>

        {/* Model Images - UPDATED with 3D Model Placeholder */}
        <div className="form-group">
          <label>Or Select a Model Image</label>
          <div className="model-images">
            <div 
              className={`model-option ${formData.imagePreview === MODEL_IMAGES.male ? 'selected' : ''}`}
              onClick={() => handleModelImageSelect(MODEL_IMAGES.male)}
            >
              <img src={MODEL_IMAGES.male} alt="Male Model" />
              <p>Male Model</p>
            </div>
            <div 
              className={`model-option ${formData.imagePreview === MODEL_IMAGES.female ? 'selected' : ''}`}
              onClick={() => handleModelImageSelect(MODEL_IMAGES.female)}
            >
              <img src={MODEL_IMAGES.female} alt="Female Model" />
              <p>Female Model</p>
            </div>
            {/* NEW 3D Model Placeholder */}
            <div 
              className={`model-option ${formData.imagePreview === MODEL_IMAGES['3d_model'] ? 'selected' : ''}`}
              onClick={() => handleModelImageSelect(MODEL_IMAGES['3d_model'])}
            >
              <img src={MODEL_IMAGES['3d_model']} alt="3D Model Placeholder" />
              <p>3D Model</p>
            </div>
          </div>
        </div>

        {/* Submit Button */}
        <button type="submit" className="btn btn-primary btn-large" disabled={loading}>
          {loading ? "Saving..." : "Continue to Catalog"}
        </button>
      </form>
    </div>
  )
}

export default UserDetailsForm
