// src/components/Recommendations.jsx
"use client"

// Component for displaying AI-powered recommendations
import { useState, useEffect, useContext } from "react"
// FIX: Import FitLookContext from App.jsx
import { FitLookContext } from "../App" 
import { apiCall } from "../services/mockApi"
import "../styles/Recommendations.css"

function Recommendations({ selectedProduct }) {
  const { handleSelectItem } = useContext(FitLookContext)
  const [recommendations, setRecommendations] = useState([])
  const [loading, setLoading] = useState(true)

  // Fetch recommendations
  useEffect(() => {
    const fetchRecommendations = async () => {
      if (!selectedProduct) return // Guard clause
      
      try {
        setLoading(true)
        // API Call to get complementary items
        const data = await apiCall("/api/recommend/get", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            product: selectedProduct,
            // Assuming user preferences are fetched from the user context/API
            userPreferences: { preferredStyle: "casual" }, 
          }),
        })

        setRecommendations(data || [])
      } catch (error) {
        console.error("Error fetching recommendations:", error)
        setRecommendations([])
      } finally {
        setLoading(false)
      }
    }

    fetchRecommendations()
  }, [selectedProduct]) 

  return (
    <div className="recommendations-section">
      <h3>Recommended Items</h3>
      {loading ? (
        <p>Loading recommendations...</p>
      ) : recommendations.length > 0 ? (
        <div className="recommendations-grid">
          {recommendations.map((item) => (
            <div 
                key={item.id} 
                className="recommendation-card" 
                // Determine the correct category key for the handler
                onClick={() => handleSelectItem(item, item.category === 'accessory' ? 'accessories' : item.category)}
            >
              <img src={item.image || "/placeholder.svg"} alt={item.title} />
              <h4>{item.title}</h4>
              <p className="category">{item.category}</p>
              <p className="price">${item.price}</p>
            </div>
          ))}
        </div>
      ) : (
        <p>No recommendations available</p>
      )}
    </div>
  )
}

export default Recommendations
