"use client"

import { useState } from "react"
import { saveUserProfile } from "../services/apiClient"
import "../styles/UserDetailsForm.css"

function UserDetailsForm({ onSubmit }) {
  const [formData, setFormData] = useState({
    name: "",
    height: "",
    weight: "",
    gender: "male",
    shoeSize: "",
    preferredStyle: "casual",
    image: null,
    imagePreview: null,
  })

  const [useModelImage, setUseModelImage] = useState(false)
  const [loading, setLoading] = useState(false)

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }))
  }

  // Handle image upload
  const handleImageUpload = (e) => {
    const file = e.target.files[0]
    if (file) {
      const reader = new FileReader()
      reader.onloadend = () => {
        setFormData((prev) => ({
          ...prev,
          image: reader.result,
          imagePreview: reader.result,
        }))
      }
      reader.readAsDataURL(file)
    }
  }

  // Handle model image selection
  const handleModelImageSelect = (modelType) => {
    // Use public folder path
    const modelPath = modelType === "male" ? "/male-model.jpg" : "/female-model.jpg"

    // Create an image element to load and convert to base64
    const img = new Image()
    img.crossOrigin = "anonymous"

    img.onload = () => {
      const canvas = document.createElement("canvas")
      canvas.width = img.width
      canvas.height = img.height
      const ctx = canvas.getContext("2d")
      ctx.drawImage(img, 0, 0)
      const base64 = canvas.toDataURL("image/jpeg")

      setFormData((prev) => ({
        ...prev,
        image: base64,
        imagePreview: base64,
      }))
      setUseModelImage(true)
    }

    img.onerror = () => {
        console.error(`[FitLook] Failed to load model image from path: ${modelPath}. Check console for details.`)
    }

    img.src = modelPath
  }

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault()

    // Validate required fields
    if (!formData.name || !formData.height || !formData.weight || !formData.shoeSize) {
      // NOTE: Using console.error instead of alert per best practice guidelines
      console.error("Please fill in all required fields") 
      return
    }

    if (!formData.image) {
      // NOTE: Using console.error instead of alert per best practice guidelines
      console.error("Please upload or select an image")
      return
    }

    setLoading(true)
    try {
      const result = await saveUserProfile({
        name: formData.name,
        // Ensure numbers are sent as integers
        height: Number.parseInt(formData.height),
        weight: Number.parseInt(formData.weight),
        gender: formData.gender,
        shoeSize: formData.shoeSize,
        preferredStyle: formData.preferredStyle,
        uploadedImage: formData.image,
      })

      console.log("[FitLook] Profile saved successfully:", result)

      onSubmit({
        ...formData,
        userId: result.user?._id || result._id,
      })
    } catch (error) {
      console.error("[FitLook] Error saving profile:", error)
      // NOTE: Fallback error display
      alert("Error saving profile: " + error.message) 
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="user-details-form">
      <h2>Tell Us About Yourself</h2>

      <form onSubmit={handleSubmit}>
        {/* Name */}
        <div className="form-group">
          <label htmlFor="name">Name *</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={(e) => setFormData((prev) => ({ ...prev, name: e.target.value }))}
            placeholder="Enter your name"
            required
          />
        </div>

        {/* Height */}
        <div className="form-group">
          <label htmlFor="height">Height (cm) *</label>
          <input
            type="number"
            id="height"
            name="height"
            value={formData.height}
            onChange={(e) => setFormData((prev) => ({ ...prev, height: e.target.value }))}
            placeholder="e.g., 170"
            required
          />
        </div>

        {/* Weight */}
        <div className="form-group">
          <label htmlFor="weight">Weight (kg) *</label>
          <input
            type="number"
            id="weight"
            name="weight"
            value={formData.weight}
            onChange={(e) => setFormData((prev) => ({ ...prev, weight: e.target.value }))}
            placeholder="e.g., 70"
            required
          />
        </div>

        {/* Gender */}
        <div className="form-group">
          <label htmlFor="gender">Gender *</label>
          <select
            id="gender"
            value={formData.gender}
            onChange={(e) => setFormData((prev) => ({ ...prev, gender: e.target.value }))}
          >
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="other">Other</option>
          </select>
        </div>

        {/* Shoe Size */}
        <div className="form-group">
          <label htmlFor="shoeSize">Shoe Size *</label>
          <input
            type="text"
            id="shoeSize"
            name="shoeSize"
            value={formData.shoeSize}
            onChange={(e) => setFormData((prev) => ({ ...prev, shoeSize: e.target.value }))}
            placeholder="e.g., 42"
            required
          />
        </div>

        {/* Preferred Style */}
        <div className="form-group">
          <label htmlFor="preferredStyle">Preferred Style *</label>
          <select
            id="preferredStyle"
            value={formData.preferredStyle}
            onChange={(e) => setFormData((prev) => ({ ...prev, preferredStyle: e.target.value }))}
          >
            <option value="casual">Casual</option>
            <option value="formal">Formal</option>
            <option value="party">Party</option>
            <option value="traditional">Traditional</option>
            <option value="sporty">Sporty</option>
          </select>
        </div>

        {/* Image Upload */}
        <div className="form-group">
          <label>Upload Your Photo *</label>
          <div className="image-upload-section">
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload} // Use the dedicated handler
              className="file-input"
            />
            {formData.imagePreview && (
              <div className="image-preview">
                <img src={formData.imagePreview || "/placeholder.svg"} alt="Preview" />
              </div>
            )}
          </div>
        </div>

        {/* Model Images */}
        <div className="form-group">
          <label>Or Select a Model Image</label>
          <div className="model-images">
            <div className="model-option" onClick={() => handleModelImageSelect("male")}>
              <img
                // FIX: Changed to root-relative path
                src="/male-model.jpg"
                alt="Male Model"
                onError={(e) => {
                  console.error("[FitLook] Failed to load male model image")
                  e.target.style.display = "none"
                }}
              />
              <p>Male Model</p>
            </div>
            <div className="model-option" onClick={() => handleModelImageSelect("female")}>
              <img
                // FIX: Changed to root-relative path
                src="/female-model.jpg"
                alt="Female Model"
                onError={(e) => {
                  console.error("[FitLook] Failed to load female model image")
                  e.target.style.display = "none"
                }}
              />
              <p>Female Model</p>
            </div>
          </div>
        </div>

        {/* Submit Button */}
        <button type="submit" className="btn btn-primary btn-large" disabled={loading}>
          {loading ? "Saving..." : "Continue to Catalog"}
        </button>
      </form>
    </div>
  )
}

export default UserDetailsForm
