// src/components/ProductCatalog.jsx
"use client"

// Component for displaying Shopify products in a grid
import { useState, useEffect, useContext } from "react"
// FIX: Import FitLookContext from App.jsx
import { FitLookContext } from "../App" 
import { apiCall } from "../services/mockApi"
import Recommendations from "./Recommendations"
import "../styles/ProductCatalog.css"

function ProductCatalog() {
  const { setCurrentStep, handleSelectItem } = useContext(FitLookContext)
  const [products, setProducts] = useState([])
  const [filteredProducts, setFilteredProducts] = useState([])
  const [selectedCategory, setSelectedCategory] = useState("top")
  const [loading, setLoading] = useState(true)
  const [selectedProduct, setSelectedProduct] = useState(null)
  const [showRecommendations, setShowRecommendations] = useState(false)

  // Function to filter products by category (moved out of useEffect for clarity)
  const filterProductsByCategory = (allProducts, category) => {
    // NOTE: This assumes products have a 'category' field (e.g., 'top', 'bottom', 'shoes', 'accessory')
    // We adjust the category check for accessories here to handle the single item/multiple item logic
    const filtered = allProducts.filter((p) => p.category === category)
    setFilteredProducts(filtered)
  }

  // 1. Fetch products from backend (Runs ONCE on mount)
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true)
        const data = await apiCall("/api/products/shopify/all") // Corrected mock path
        setProducts(data)
        // Set initial filtered products after fetching
        filterProductsByCategory(data, "top") 
      } catch (error) {
        console.error("Error fetching products:", error)
      } finally {
        setLoading(false)
      }
    }
    fetchProducts()
  }, [])

  // 2. Filter products when category changes (Fixes missing dependency warning)
  useEffect(() => {
    // Only filter if products have been fetched
    if (products.length > 0) {
      filterProductsByCategory(products, selectedCategory)
    }
  }, [selectedCategory, products]) 

  // Handle category change
  const handleCategoryChange = (category) => {
    setSelectedCategory(category)
    setSelectedProduct(null)
    setShowRecommendations(false)
  }

  // Handle product selection
  const handleProductSelect = async (product) => {
    // Only set selectedProduct if it's not an accessory (since accessories can be multiple)
    if (selectedCategory !== "accessory") {
        setSelectedProduct(product)
    }

    // Use 'accessories' for the category to match the state in App.jsx
    const categoryKey = selectedCategory === "accessory" ? "accessories" : selectedCategory;
    handleSelectItem(product, categoryKey)

    // Trigger recommendation fetch (API call is already inside Recommendations component useEffect)
    setShowRecommendations(true) 
  }

  return (
    <div className="product-catalog">
      <h2>Select Your Outfit</h2>

      {/* Category Filter */}
      <div className="category-filter">
        <button
          className={`category-btn ${selectedCategory === "top" ? "active" : ""}`}
          onClick={() => handleCategoryChange("top")}
        >
          Tops
        </button>
        <button
          className={`category-btn ${selectedCategory === "bottom" ? "active" : ""}`}
          onClick={() => handleCategoryChange("bottom")}
        >
          Bottoms
        </button>
        <button
          className={`category-btn ${selectedCategory === "shoes" ? "active" : ""}`}
          onClick={() => handleCategoryChange("shoes")}
        >
          Shoes
        </button>
        <button
          className={`category-btn ${selectedCategory === "accessory" ? "active" : ""}`}
          onClick={() => handleCategoryChange("accessory")}
        >
          Accessories
        </button>
      </div>

      {/* Products Grid */}
      <div className="products-grid">
        {loading ? (
          <p>Loading products...</p>
        ) : filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
            <div
              key={product.id}
              className={`product-card ${selectedProduct?.id === product.id && selectedCategory !== 'accessory' ? "selected" : ""}`}
              onClick={() => handleProductSelect(product)}
            >
              <img src={product.image || "/placeholder.svg"} alt={product.title} />
              <h3>{product.title}</h3>
              <p className="price">${product.price}</p>
            </div>
          ))
        ) : (
          <p>No products found in this category</p>
        )}
      </div>

      {/* Recommendations */}
      {showRecommendations && selectedProduct && <Recommendations selectedProduct={selectedProduct} />}

      {/* Navigation Buttons */}
      <div className="catalog-actions">
        <button className="btn btn-secondary" onClick={() => setCurrentStep("details")}>
          Back
        </button>
        <button className="btn btn-primary" onClick={() => setCurrentStep("preview")} disabled={!selectedProduct}>
          Preview Outfit
        </button>
      </div>
    </div>
  )
}

export default ProductCatalog
