"use client"

import { useState } from "react"
import { saveUserProfile } from "../services/apiClient"
import "../styles/UserDetailsForm.css"

function UserDetailsForm({ onSubmit }) {
  const [formData, setFormData] = useState({
    name: "",
    height: "",
    weight: "",
    gender: "male",
    shoeSize: "",
    preferredStyle: "casual",
    image: null,
    imagePreview: null,
  })

  // State for user-facing error messages (replaces alerts)
  const [errorMessage, setErrorMessage] = useState("")
  const [loading, setLoading] = useState(false)

  // Handle image upload from file input
  const handleImageUpload = (e) => {
    setErrorMessage(""); // Clear any previous errors
    const file = e.target.files[0]
    if (file) {
      const reader = new FileReader()
      reader.onloadend = () => {
        setFormData((prev) => ({
          ...prev,
          image: reader.result,
          imagePreview: reader.result,
        }))
      }
      reader.readAsDataURL(file)
    }
  }

  // Handle model image selection
  const handleModelImageSelect = (modelType) => {
    setErrorMessage(""); // Clear any previous errors
    // Use public folder path. Ensure these files are in your project's /public directory.
    const modelPath = modelType === "male" ? "/male-model.jpg" : "/female-model.jpg"

    // Create an image element to load and convert to base64
    const img = new Image()
    img.crossOrigin = "anonymous"

    img.onload = () => {
      const canvas = document.createElement("canvas")
      canvas.width = img.width
      canvas.height = img.height
      const ctx = canvas.getContext("2d")
      ctx.drawImage(img, 0, 0)
      const base64 = canvas.toDataURL("image/jpeg")

      setFormData((prev) => ({
        ...prev,
        image: base64,
        imagePreview: base64,
      }))
      // Note: Removed setUseModelImage as it was unused
    }

    img.onerror = () => {
        // This is triggered if the image file is not found (404 error).
        const msg = `Failed to load model image from path: ${modelPath}. Please ensure the file is in your /public folder.`
        console.error(`[FitLook] ${msg}`)
        setErrorMessage(msg);
    }

    img.src = modelPath
  }

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault()
    setErrorMessage("") // Clear previous errors

    // 1. Validate required fields
    if (!formData.name || !formData.height || !formData.weight || !formData.shoeSize) {
      const msg = "Please fill in all required fields."
      console.error(msg); 
      setErrorMessage(msg)
      return
    }

    // 2. Validate image is selected
    if (!formData.image) {
      const msg = "Please upload or select an image."
      console.error(msg);
      setErrorMessage(msg)
      return
    }

    setLoading(true)
    try {
      const result = await saveUserProfile({
        name: formData.name,
        // Ensure numbers are sent as integers
        height: Number.parseInt(formData.height),
        weight: Number.parseInt(formData.weight),
        gender: formData.gender,
        shoeSize: formData.shoeSize,
        preferredStyle: formData.preferredStyle,
        uploadedImage: formData.image,
      })

      console.log("[FitLook] Profile saved successfully:", result)

      onSubmit({
        ...formData,
        userId: result.user?._id || result._id,
      })
    } catch (error) {
      console.error("[FitLook] Error saving profile:", error)
      // 3. Replaced alert with state update for submission error
      setErrorMessage("Error saving profile: " + (error.message || "An unknown error occurred."))
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="user-details-form">
      <h2>Tell Us About Yourself</h2>

      {/* Visual Error Message Display */}
      {errorMessage && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-md mb-6" role="alert">
              <p className="font-bold">Error</p>
              <p className="text-sm">{errorMessage}</p>
          </div>
      )}

      <form onSubmit={handleSubmit}>
        {/* Name */}
        <div className="form-group">
          <label htmlFor="name">Name *</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={(e) => setFormData((prev) => ({ ...prev, name: e.target.value }))}
            placeholder="Enter your name"
            required
          />
        </div>

        {/* Height */}
        <div className="form-group">
          <label htmlFor="height">Height (cm) *</label>
          <input
            type="number"
            id="height"
            name="height"
            value={formData.height}
            onChange={(e) => setFormData((prev) => ({ ...prev, height: e.target.value }))}
            placeholder="e.g., 170"
            required
          />
        </div>

        {/* Weight */}
        <div className="form-group">
          <label htmlFor="weight">Weight (kg) *</label>
          <input
            type="number"
            id="weight"
            name="weight"
            value={formData.weight}
            onChange={(e) => setFormData((prev) => ({ ...prev, weight: e.target.value }))}
            placeholder="e.g., 70"
            required
          />
        </div>

        {/* Gender */}
        <div className="form-group">
          <label htmlFor="gender">Gender *</label>
          <select
            id="gender"
            value={formData.gender}
            onChange={(e) => setFormData((prev) => ({ ...prev, gender: e.target.value }))}
          >
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="other">Other</option>
          </select>
        </div>

        {/* Shoe Size */}
        <div className="form-group">
          <label htmlFor="shoeSize">Shoe Size *</label>
          <input
            type="text"
            id="shoeSize"
            name="shoeSize"
            value={formData.shoeSize}
            onChange={(e) => setFormData((prev) => ({ ...prev, shoeSize: e.target.value }))}
            placeholder="e.g., 42"
            required
          />
        </div>

        {/* Preferred Style */}
        <div className="form-group">
          <label htmlFor="preferredStyle">Preferred Style *</label>
          <select
            id="preferredStyle"
            value={formData.preferredStyle}
            onChange={(e) => setFormData((prev) => ({ ...prev, preferredStyle: e.target.value }))}
          >
            <option value="casual">Casual</option>
            <option value="formal">Formal</option>
            <option value="party">Party</option>
            <option value="traditional">Traditional</option>
            <option value="sporty">Sporty</option>
          </select>
        </div>

        {/* Image Upload */}
        <div className="form-group">
          <label>Upload Your Photo *</label>
          <div className="image-upload-section">
            <input
              type="file"
              accept="image/*"
              // Uses the dedicated handler
              onChange={handleImageUpload} 
              className="file-input"
            />
            {formData.imagePreview && (
              <div className="image-preview">
                <img src={formData.imagePreview || "/placeholder.svg"} alt="Preview" />
              </div>
            )}
          </div>
        </div>

        {/* Model Images */}
        <div className="form-group">
          <label>Or Select a Model Image</label>
          <div className="model-images">
            <div className="model-option" onClick={() => handleModelImageSelect("male")}>
              <img
                // Path is correct, expecting .jpg in public folder
                src="/male-model.jpg"
                alt="Male Model"
                onError={(e) => {
                  e.target.style.display = "none"
                }}
              />
              <p>Male Model</p>
            </div>
            <div className="model-option" onClick={() => handleModelImageSelect("female")}>
              <img
                // Path is correct, expecting .jpg in public folder
                src="/female-model.jpg"
                alt="Female Model"
                onError={(e) => {
                  e.target.style.display = "none"
                }}
              />
              <p>Female Model</p>
            </div>
          </div>
        </div>

        {/* Submit Button */}
        <button type="submit" className="btn btn-primary btn-large" disabled={loading}>
          {loading ? "Saving..." : "Continue to Catalog"}
        </button>
      </form>
    </div>
  )
}

export default UserDetailsForm
