// src/components/OutfitPreview.jsx
"use client"

// Component for virtual try-on with image overlay
import { useContext, useRef, useEffect } from "react"
import { FitLookContext } from "../App" 
import "../styles/OutfitPreview.css"

function OutfitPreview() {
  // Destructure selectedItems for cleaner use
  const { userImage, selectedItems, setCurrentStep } = useContext(FitLookContext)
  const canvasRef = useRef(null)

  // Helper function to draw clothing items on canvas
  const drawClothingItem = (ctx, itemImage, category, imageHeight) => {
    const img = new Image()
    img.crossOrigin = "anonymous"
    img.onload = () => {
      // Basic positioning logic based on category
      let y = 0
      let height = imageHeight / 3 
      let x = 0
      let width = ctx.canvas.width

      // Simple vertical segmentation (Needs refinement for real use)
      if (category === "top") {
        y = imageHeight * 0.1
      } else if (category === "bottom") {
        y = imageHeight * 0.4
      } else if (category === "shoes") {
        y = imageHeight * 0.75
        height = imageHeight * 0.2 // Shoes are typically shorter
      } else if (category === "accessory") {
        y = imageHeight * 0.05 // Place near the top/head area
        width = ctx.canvas.width * 0.3 // Accessories are smaller
        height = imageHeight * 0.15
        x = ctx.canvas.width * 0.65 // Place on the side
      }
      
      // Draw with transparency (basic overlay simulation)
      ctx.globalAlpha = 0.8
      ctx.drawImage(img, x, y, width, height) 
      ctx.globalAlpha = 1
    }
    img.src = itemImage
  }
    
  // Draw outfit preview on canvas
  useEffect(() => {
    if (canvasRef.current && userImage) {
      const canvas = canvasRef.current
      const ctx = canvas.getContext("2d")

      // Helper to load and draw an image
      const loadImageAndDraw = (src, drawFunc) => {
        const img = new Image()
        img.crossOrigin = "anonymous"
        img.onload = () => drawFunc(img)
        img.onerror = () => console.error("Failed to load image:", src)
        img.src = src
      }

      // Load base image and start drawing process
      loadImageAndDraw(userImage, (img) => {
        // Set canvas size to match image
        canvas.width = img.width
        canvas.height = img.height

        // Draw base image
        ctx.clearRect(0, 0, canvas.width, canvas.height); // Clear before redrawing
        ctx.drawImage(img, 0, 0)

        // Draw main items
        const mainItems = [selectedItems.top, selectedItems.bottom, selectedItems.shoes]
        
        mainItems.forEach((item) => {
          if (item && item.image) {
            drawClothingItem(ctx, item.image, item.category, img.height)
          }
        })
        
        // Iterate over the accessories array for drawing
        selectedItems.accessories.forEach(accessory => {
            if (accessory && accessory.image) {
                drawClothingItem(ctx, accessory.image, 'accessory', img.height);
            }
        });
      })
    } 
  }, [userImage, selectedItems]) // Dependency array is CRITICAL

  // Check if complete outfit is selected (Top + Bottom)
  const isCompleteOutfit = selectedItems.top && selectedItems.bottom

  // Handle complete outfit purchase
  const handleBuyCompleteOutfit = async () => {
    const itemsForCart = [];
    if (selectedItems.top) itemsForCart.push({ id: selectedItems.top.id, category: 'top' });
    if (selectedItems.bottom) itemsForCart.push({ id: selectedItems.bottom.id, category: 'bottom' });
    if (selectedItems.shoes) itemsForCart.push({ id: selectedItems.shoes.id, category: 'shoes' });
    
    // Add all selected accessories
    selectedItems.accessories.forEach(a => itemsForCart.push({ id: a.id, category: 'accessory' }));
    

    try {
      // POST to backend to get the Shopify checkout URL (Mocked response structure)
      const response = await fetch("/api/cart/add-outfit", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ items: itemsForCart }),
      })

      if (response.ok) {
        // Simulate a successful response with a cart URL
        const data = { cartUrl: 'https://checkout.shopify.com/12345/checkout' }; 

        if (data.cartUrl) {
          // NOTE: This will redirect the user to the cart/checkout page.
          window.location.href = data.cartUrl 
        } else {
          console.log("Checkout URL not received from the server.")
        }
      } else {
        console.log("Failed to create cart URL: " + response.statusText)
      }
    } catch (error) {
      console.error("Error adding to cart:", error)
    }
  }

  return (
    <div className="outfit-preview">
      <h2>Your Outfit Preview</h2>

      {/* Canvas for outfit preview */}
      <div className="preview-container">
        {userImage ? (
          <canvas ref={canvasRef} className="preview-canvas" />
        ) : (
          <div className="placeholder-preview">Please select an image and items in the catalog.</div>
        )}
      </div>

      {/* Selected items summary (simplified for brevity) */}
      <div className="selected-items-summary">
        <h3>Selected Items</h3>
        {selectedItems.top && (
          <div className="item-summary">
            <span>Top:</span>
            <span>{selectedItems.top.title}</span>
            <span>${selectedItems.top.price}</span>
          </div>
        )}
        {selectedItems.bottom && (
          <div className="item-summary">
            <span>Bottom:</span>
            <span>{selectedItems.bottom.title}</span>
            <span>${selectedItems.bottom.price}</span>
          </div>
        )}
        {selectedItems.shoes && (
          <div className="item-summary">
            <span>Shoes:</span>
            <span>{selectedItems.shoes.title}</span>
            <span>${selectedItems.shoes.price}</span>
          </div>
        )}
        {selectedItems.accessories.map(item => (
            <div key={item.id} className="item-summary accessory-summary">
                <span>Accessory:</span>
                <span>{item.title}</span>
                <span>${item.price}</span>
            </div>
        ))}
      </div>

      {/* Discount banner */}
      {isCompleteOutfit && (
        <div className="discount-banner">
          <h3>Complete the Look!</h3>
          <p>Get **10% off** when you buy the complete outfit</p>
          <button className="btn btn-primary" onClick={handleBuyCompleteOutfit}>
            Buy Complete Outfit
          </button>
        </div>
      )}

      {/* Navigation buttons */}
      <div className="preview-actions">
        <button className="btn btn-secondary" onClick={() => setCurrentStep("catalog")}>
          Back to Catalog
        </button>
        {!isCompleteOutfit && (
          <button className="btn btn-primary" onClick={() => setCurrentStep("catalog")}>
            Add More Items
          </button>
        )}
      </div>
    </div>
  )
}

export default OutfitPreview
