// src/components/AdminPanel.jsx
"use client"

// Admin panel for managing discounts and product categories
import { useState } from "react"
import { apiCall } from "../services/mockApi.js" // Assuming mockApi handles auth logic
import "../styles/AdminPanel.css"

function AdminPanel() {
  const [adminPassword, setAdminPassword] = useState("")
  const [isAuthenticated, setIsAuthenticated] = useState(false)
  const [discountRules, setDiscountRules] = useState([])
  const [newRule, setNewRule] = useState({
    name: "",
    description: "",
    discountPercentage: 10,
    requiredItems: { top: true, bottom: true },
  })
  const [loading, setLoading] = useState(false)

  // Login handler
  const handleAdminLogin = async (e) => {
    e.preventDefault()
    setLoading(true)
    try {
      // Backend should handle password verification and return data/success flag
      const result = await apiCall("/api/admin/login", { 
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ adminPassword: adminPassword }),
      })

      // Check if the backend response indicates success
      if (result.success) {
        setIsAuthenticated(true)
        // Fetch rules after successful authentication
        const rulesData = await apiCall("/api/admin/discount-rules") 
        setDiscountRules(Array.isArray(rulesData) ? rulesData : [])
      } else {
        alert("Invalid credentials or login failed.")
      }
    } catch (error) {
      console.error("Error during login:", error)
      alert("Login failed: " + error.message)
    } finally {
      setLoading(false)
    }
  }

  // Create rule handler
  const handleCreateRule = async (e) => {
    e.preventDefault()
    setLoading(true)

    try {
      // POST to backend to save the new rule
      const result = await apiCall("/api/admin/discount-rules", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        // Pass password/token securely (mockApi should handle this via context/header)
        body: JSON.stringify({ ...newRule, adminPassword: adminPassword }), 
      })

      if (result.success || result.rule) {
        const rule = result.rule || result
        setDiscountRules([...discountRules, rule])
        setNewRule({
          name: "",
          description: "",
          discountPercentage: 10,
          requiredItems: { top: true, bottom: true },
        })
        alert("Discount rule created successfully")
      } else {
         alert("Failed to create rule: " + (result.message || "Unknown error"))
      }
    } catch (error) {
      console.error("Error creating rule:", error)
      alert("Failed to create discount rule: " + error.message)
    } finally {
      setLoading(false)
    }
  }

  if (!isAuthenticated) {
    return (
      <div className="admin-login">
        <h2>Admin Panel</h2>
        <form onSubmit={handleAdminLogin}>
          <div className="form-group">
            <label htmlFor="password">Admin Password</label>
            <input
              type="password"
              id="password"
              value={adminPassword}
              onChange={(e) => setAdminPassword(e.target.value)}
              placeholder="Enter admin password"
              required
            />
          </div>
          <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? "Logging in..." : "Login"}
          </button>
        </form>
      </div>
    )
  }

  return (
    <div className="admin-panel">
      {/* ... (Authenticated JSX is unchanged) ... */}
      <h2>Admin Panel</h2>

      {/* Create New Discount Rule */}
      <section className="admin-section">
        <h3>Create Discount Rule</h3>
        <form onSubmit={handleCreateRule}>
          <div className="form-group">
            <label htmlFor="ruleName">Rule Name</label>
            <input
              type="text"
              id="ruleName"
              value={newRule.name}
              onChange={(e) => setNewRule({ ...newRule, name: e.target.value })}
              placeholder="e.g., Complete Outfit Discount"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="description">Description</label>
            <textarea
              id="description"
              value={newRule.description}
              onChange={(e) => setNewRule({ ...newRule, description: e.target.value })}
              placeholder="Describe the discount rule"
            />
          </div>

          <div className="form-group">
            <label htmlFor="discount">Discount Percentage</label>
            <input
              type="number"
              id="discount"
              value={newRule.discountPercentage}
              onChange={(e) => setNewRule({ ...newRule, discountPercentage: Number.parseInt(e.target.value) })}
              min="0"
              max="100"
              required
            />
          </div>

          <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? "Creating..." : "Create Rule"}
          </button>
        </form>
      </section>

      {/* Existing Discount Rules */}
      <section className="admin-section">
        <h3>Existing Discount Rules</h3>
        {discountRules.length > 0 ? (
          <div className="rules-list">
            {discountRules.map((rule) => (
              // Assuming rules have a unique ID like _id
              <div key={rule._id || rule.name} className="rule-card"> 
                <h4>{rule.name}</h4>
                <p>{rule.description}</p>
                <p className="discount-badge">{rule.discountPercentage}% OFF</p>
              </div>
            ))}
          </div>
        ) : (
          <p>No discount rules created yet</p>
        )}
      </section>
    </div>
  )
}

export default AdminPanel
