// src/components/Recommendations.jsx
"use client"

// Component for displaying AI-powered recommendations
import { useState, useEffect, useContext } from "react"
import { FitLookContext } from "../App" 
import "../styles/Recommendations.css"

// --- MOCK RECOMMENDATION LOGIC (Replaces API call) ---
const getMockRecommendations = (product) => {
    // Simple logic: recommend a top if a bottom is selected, and vice versa.
    if (!product) return [];

    if (product.category === 'top') {
        return [
            { id: "mock-r1", title: "Dark Wash Denim", category: "bottom", price: 65.00, image: "/images/mock-bottom-3.jpg" },
            { id: "mock-r2", title: "Trail Runners", category: "shoes", price: 99.00, image: "/images/mock-shoes-3.jpg" },
        ];
    } else if (product.category === 'bottom') {
        return [
            { id: "mock-r3", title: "Soft Cotton Hoodie", category: "top", price: 79.00, image: "/images/mock-top-3.jpg" },
            { id: "mock-r4", title: "Luxury Watch", category: "accessory", price: 199.00, image: "/images/mock-accessory-3.jpg" },
        ];
    } else if (product.category === 'shoes') {
         return [
            { id: "mock-r5", title: "Solid Black Tee", category: "top", price: 25.00, image: "/images/mock-top-4.jpg" },
            { id: "mock-r6", title: "Cargo Shorts", category: "bottom", price: 40.00, image: "/images/mock-bottom-4.jpg" },
        ];
    } else {
        return [];
    }
}
// --------------------------------------------------------

function Recommendations({ selectedProduct }) {
  const { handleSelectItem } = useContext(FitLookContext)
  const [recommendations, setRecommendations] = useState([])
  const [loading, setLoading] = useState(true)

  // Fetch recommendations
  useEffect(() => {
    const fetchRecommendations = async () => {
      if (!selectedProduct) return // Guard clause
      
      try {
        setLoading(true)
        
        // Simulate API call delay
        setTimeout(() => {
          const data = getMockRecommendations(selectedProduct);
          setRecommendations(data || [])
          setLoading(false)
        }, 400);

      } catch (error) {
        console.error("Error fetching recommendations:", error)
        setRecommendations([])
        setLoading(false)
      } 
    }

    fetchRecommendations()
  }, [selectedProduct]) 

  return (
    <div className="recommendations-section">
      <h3>Recommended Items</h3>
      {loading ? (
        <p>Loading recommendations...</p>
      ) : recommendations.length > 0 ? (
        <div className="recommendations-grid">
          {recommendations.map((item) => (
            <div 
                key={item.id} 
                className="recommendation-card" 
                // Determine the correct category key for the handler
                onClick={() => handleSelectItem(item, item.category === 'accessory' ? 'accessories' : item.category)}
            >
              <img src={item.image || "/placeholder.svg"} alt={item.title} />
              <h4>{item.title}</h4>
              <p className="category">{item.category}</p>
              <p className="price">${item.price}</p>
            </div>
          ))}
        </div>
      ) : (
        <p>No recommendations available for this item.</p>
      )}
    </div>
  )
}

export default Recommendations
